package com.syntax.class07;

public class DoWhilePratice {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
	int num= 1;
	
	do {
		System.out.print(num+ " ");
	num++; 
	}while (num<=30);
	
	System.out.println("--------------");
	
	
	int num2= 70;
	 do {
		 System.out.print(num2+" ");
		 num2-=2;
	 }while (num2>=30);
	
	
	
	}

}
